import { useState } from 'react';

function BotaoExcluir({ onClick }) {
  const [hover, setHover] = useState(false);

  return (
    <button
      className="botao botao-excluir"
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
      onClick={onClick}
    >
      {hover ? '❌' : 'Excluir'}
    </button>
  );
}

export default BotaoExcluir;
