import React, { useState } from 'react';
import api from '../services/api';
import { useNavigate } from 'react-router-dom';

function Login() {
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [erro, setErro] = useState('');
  const [modoCadastro, setModoCadastro] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErro('');

    try {
      if (modoCadastro) {
        // Cadastro de novo usuário
        await api.post('/usuarios/cadastrar', { nome, email, senha });
        setModoCadastro(false);
        setErro('✅ Cadastro realizado com sucesso! Faça login.');
        setNome('');
        setEmail('');
        setSenha('');
      } else {
        // Login de usuário existente
        const response = await api.post('/usuarios/login', { email, senha });
        localStorage.setItem('token', response.data.token);
        navigate('/clientes');
      }
    } catch (err) {
      setErro(modoCadastro ? '❌ Erro ao cadastrar usuário' : '❌ Email ou senha inválidos');
      console.error(err);
    }
  };

  return (
    <div style={{ padding: '2rem', maxWidth: '400px', margin: 'auto' }}>
      <h2>{modoCadastro ? 'Cadastro de Usuário' : 'Login'}</h2>
      <form onSubmit={handleSubmit}>
        {modoCadastro && (
          <>
            <input
              type="text"
              placeholder="Nome"
              value={nome}
              onChange={e => {
                setNome(e.target.value);
                setErro('');
              }}
              required
            /><br /><br />
          </>
        )}
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={e => {
            setEmail(e.target.value);
            setErro('');
          }}
          required
        /><br /><br />
        <input
          type="password"
          placeholder="Senha"
          value={senha}
          onChange={e => {
            setSenha(e.target.value);
            setErro('');
          }}
          required
        /><br /><br />
        <button type="submit">
          {modoCadastro ? 'Cadastrar' : 'Entrar'}
        </button>
      </form>

      <button
        style={{ marginTop: '1rem' }}
        onClick={() => {
          setModoCadastro(!modoCadastro);
          setErro('');
        }}
      >
        {modoCadastro ? 'Já tenho conta' : 'Criar nova conta'}
      </button>

      {erro && (
        <p style={{ color: modoCadastro ? 'green' : 'red', marginTop: '1rem' }}>
          {erro}
        </p>
      )}
    </div>
  );
}

export default Login;
