import React, { useState, useEffect } from 'react';
import api from '../services/api';
import './Venda.css';

function Venda() {
  const [clientes, setClientes] = useState([]);
  const [produtos, setProdutos] = useState([]);
  const [clienteId, setClienteId] = useState('');
  const [itens, setItens] = useState([{ produto_id: '', quantidade: 1, preco_unitario: 0 }]);
  const [valorTotal, setValorTotal] = useState(0);

  useEffect(() => {
    const token = localStorage.getItem('token');
    api.get('/clientes', { headers: { Authorization: `Bearer ${token}` } })
      .then(res => setClientes(res.data));
    api.get('/produtos', { headers: { Authorization: `Bearer ${token}` } })
      .then(res => setProdutos(res.data));
  }, []);

  useEffect(() => {
    const total = itens.reduce((acc, item) => acc + item.quantidade * item.preco_unitario, 0);
    setValorTotal(total);
  }, [itens]);

  const handleItemChange = (index, field, value) => {
    const novosItens = [...itens];
    novosItens[index][field] = field === 'quantidade' || field === 'preco_unitario' ? Number(value) : value;
    setItens(novosItens);
  };

  const adicionarItem = () => {
    setItens([...itens, { produto_id: '', quantidade: 1, preco_unitario: 0 }]);
  };

  const removerItem = (index) => {
    const novosItens = itens.filter((_, i) => i !== index);
    setItens(novosItens);
  };

  const finalizarVenda = async () => {
    const token = localStorage.getItem('token');
    try {
      const payload = {
        cliente_id: clienteId || null,
        itens
      };
      const res = await api.post('/vendas', payload, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert(`‚úÖ Venda registrada! ID: ${res.data.venda_id}`);
      setClienteId('');
      setItens([{ produto_id: '', quantidade: 1, preco_unitario: 0 }]);
    } catch (err) {
      console.error(err);
      alert('‚ùå Erro ao registrar venda');
    }
  };

  return (
    <div className="venda-container">
      <h2>üõí Registrar Venda</h2>

      <select value={clienteId} onChange={e => setClienteId(e.target.value)}>
        <option value="">Cliente (opcional)</option>
        {clientes.map(c => (
          <option key={c.id} value={c.id}>{c.nome}</option>
        ))}
      </select>

      {itens.map((item, index) => (
        <div key={index} className="item-venda">
          <select
            value={item.produto_id}
            onChange={e => handleItemChange(index, 'produto_id', e.target.value)}
          >
            <option value="">Produto</option>
            {produtos.map(p => (
              <option key={p.id} value={p.id}>{p.nome}</option>
            ))}
          </select>

          <input
            type="number"
            placeholder="Qtd"
            value={item.quantidade}
            onChange={e => handleItemChange(index, 'quantidade', e.target.value)}
            min={1}
          />

          <input
            type="number"
            placeholder="Pre√ßo unit√°rio"
            value={item.preco_unitario}
            onChange={e => handleItemChange(index, 'preco_unitario', e.target.value)}
            min={0}
            step="0.01"
          />

          <button onClick={() => removerItem(index)}>üóëÔ∏è</button>
        </div>
      ))}

      <button onClick={adicionarItem}>‚ûï Adicionar Produto</button>

      <h3>Total: R$ {valorTotal.toFixed(2)}</h3>

      <button className="finalizar-btn" onClick={finalizarVenda}>üíæ Finalizar Venda</button>
    </div>
  );
}

export default Venda;
