import React, { useEffect, useState } from 'react';
import api from '../services/api';
import { useNavigate } from 'react-router-dom';

// Função para remover acentos
const removerAcentos = (texto) => {
  return texto.normalize('NFD').replace(/[\u0300-\u036f]/g, '');
};

function Produtos() {
  const [produtos, setProdutos] = useState([]);
  const [busca, setBusca] = useState('');
  const [mensagem, setMensagem] = useState('');
  const [form, setForm] = useState({
    nome: '',
    descricao: '',
    preco: '',
    quantidade: '',
    categoria: ''
  });
  const [modoEdicao, setModoEdicao] = useState(false);
  const [idEditando, setIdEditando] = useState(null);
  const [reposicao, setReposicao] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    carregarProdutos();
  }, []);

  const carregarProdutos = async () => {
    const token = localStorage.getItem('token');
    try {
      const res = await api.get('/produtos', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setProdutos(res.data);
    } catch (err) {
      console.error('Erro ao carregar produtos:', err);
      setMensagem('❌ Erro ao carregar produtos');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      if (modoEdicao) {
        await api.put(`/produtos/${idEditando}`, form, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMensagem('✅ Produto atualizado com sucesso!');
      } else {
        await api.post('/produtos', form, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMensagem('✅ Produto cadastrado com sucesso!');
      }
      setForm({ nome: '', descricao: '', preco: '', quantidade: '', categoria: '' });
      setModoEdicao(false);
      setIdEditando(null);
      carregarProdutos();
    } catch (err) {
      console.error('Erro ao salvar produto:', err);
      setMensagem('❌ Erro ao salvar produto');
    }
  };

  const editarProduto = (produto) => {
    setForm({
      nome: produto.nome,
      descricao: produto.descricao,
      preco: produto.preco,
      quantidade: produto.quantidade,
      categoria: produto.categoria
    });
    setModoEdicao(true);
    setIdEditando(produto.id);
  };

  const excluirProduto = async (id) => {
    const confirmar = window.confirm('Tem certeza que deseja excluir este produto?');
    if (!confirmar) return;

    const token = localStorage.getItem('token');
    try {
      await api.delete(`/produtos/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMensagem('✅ Produto excluído com sucesso!');
      carregarProdutos();
    } catch (err) {
      console.error('Erro ao excluir produto:', err);
      setMensagem('❌ Erro ao excluir produto');
    }
  };

  const reporEstoque = async (id) => {
    const token = localStorage.getItem('token');
    try {
      await api.put(`/produtos/${id}/repor`, { quantidade_repor: parseInt(reposicao) }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMensagem(`✅ Estoque do produto ${id} atualizado com +${reposicao}`);
      setReposicao('');
      carregarProdutos();
    } catch (err) {
      console.error('Erro ao repor estoque:', err);
      setMensagem('❌ Erro ao repor estoque');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Cadastro de Produtos</h2>

      <form onSubmit={handleSubmit} style={{ marginBottom: '2rem' }}>
        <input
          type="text"
          placeholder="Nome"
          value={form.nome}
          onChange={e => setForm({ ...form, nome: e.target.value })}
          required
        />{' '}
        <input
          type="text"
          placeholder="Descrição"
          value={form.descricao}
          onChange={e => setForm({ ...form, descricao: e.target.value })}
        />{' '}
        <input
          type="number"
          placeholder="Preço"
          value={form.preco}
          onChange={e => setForm({ ...form, preco: e.target.value })}
          required
        />{' '}
        <input
          type="number"
          placeholder="Quantidade"
          value={form.quantidade}
          onChange={e => setForm({ ...form, quantidade: e.target.value })}
          required
        />{' '}
        <input
          type="text"
          placeholder="Categoria"
          value={form.categoria}
          onChange={e => setForm({ ...form, categoria: e.target.value })}
        />{' '}
        <button type="submit">{modoEdicao ? 'Atualizar' : 'Cadastrar'}</button>
      </form>

      <input
        type="text"
        placeholder="Buscar por nome..."
        value={busca}
        onChange={e => setBusca(e.target.value)}
        style={{ marginBottom: '1rem', padding: '0.5rem', width: '300px' }}
      />

      {mensagem && (
        <p style={{ color: mensagem.includes('✅') ? 'green' : 'red' }}>{mensagem}</p>
      )}

      <table border="1" cellPadding="10" style={{ width: '100%', marginTop: '1rem' }}>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Descrição</th>
            <th>Preço</th>
            <th>Quantidade</th>
            <th>Categoria</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {produtos
            .filter(p =>
              removerAcentos(p.nome.toLowerCase()).includes(
                removerAcentos(busca.toLowerCase())
              )
            )
            .map(produto => (
              <tr key={produto.id}>
                <td>{produto.nome}</td>
                <td>{produto.descricao}</td>
                <td>R$ {parseFloat(produto.preco).toFixed(2)}</td>
                <td>{produto.quantidade}</td>
                <td>{produto.categoria}</td>
                <td>
                  <button onClick={() => editarProduto(produto)}>Editar</button>{' '}
                  <button
                    onClick={() => excluirProduto(produto.id)}
                    style={{ backgroundColor: 'red', color: 'white' }}
                  >
                    Excluir
                  </button>{' '}
                  <input
                    type="number"
                    placeholder="+Estoque"
                    value={reposicao}
                    onChange={e => setReposicao(e.target.value)}
                    style={{ width: '80px' }}
                  />
                  <button onClick={() => reporEstoque(produto.id)}>Repor</button>
                </td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
}

export default Produtos;
