import React from 'react';
import { Link } from 'react-router-dom';

function Navbar() {
  const navbarStyle = {
    backgroundColor: '#1e1e1e',
    padding: '1rem 2rem',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    color: '#f3f3f3',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.5)',
    position: 'sticky',
    top: 0,
    zIndex: 1000
  };

  const logoStyle = {
    fontSize: '1.5rem',
    fontWeight: 'bold',
    color: '#3b82f6'
  };

  const linkListStyle = {
    display: 'flex',
    gap: '2rem',
    listStyle: 'none',
    margin: 0,
    padding: 0
  };

  const linkStyle = {
    textDecoration: 'none',
    color: '#f3f3f3',
    fontWeight: 500
  };

  const linkHoverStyle = {
    color: '#3b82f6'
  };

  const userStyle = {
    display: 'flex',
    alignItems: 'center',
    gap: '1rem'
  };

  const buttonStyle = {
    backgroundColor: '#ef4444',
    color: 'white',
    border: 'none',
    padding: '0.5rem 1rem',
    borderRadius: '6px',
    cursor: 'pointer'
  };

  return (
    <nav style={navbarStyle}>
      <div style={logoStyle}>ðŸš€ MyDashboard</div>
      <ul style={linkListStyle}>
        <li><Link to="/clientes" style={linkStyle}>ðŸ‘¥ Clientes</Link></li>
        <li><Link to="/produtos" style={linkStyle}>ðŸ“¦ Produtos</Link></li>
        <li><Link to="/vendas" style={linkStyle}>ðŸ’° Vendas</Link></li>
      </ul>
      <div style={userStyle}>
        <span>ðŸ‘¤ Tommysfpcs</span>
        <button style={buttonStyle}>Sair</button>
      </div>
    </nav>
  );
}

export default Navbar;
