import React, { useState, useEffect } from 'react';
import api from '../services/api';
import { useParams, useNavigate } from 'react-router-dom';

function EditarCliente() {
  const { id } = useParams();
  const [nome, setNome] = useState('');
  const [contato, setContato] = useState('');
  const [endereco, setEndereco] = useState('');
  const [mensagem, setMensagem] = useState('');
  const [carregando, setCarregando] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const carregarCliente = async () => {
      const token = localStorage.getItem('token');
      try {
        const res = await api.get(`/clientes/${id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });

        // Verifica se os dados vieram corretamente
        if (res.data && res.data.nome) {
          setNome(res.data.nome);
          setContato(res.data.contato);
          setEndereco(res.data.endereco);
          setMensagem('');
        } else {
          setMensagem('❌ Cliente não encontrado');
        }
      } catch (err) {
        setMensagem('❌ Erro ao carregar cliente');
        console.error(err);
      } finally {
        setCarregando(false);
      }
    };

    carregarCliente();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      await api.put(`/clientes/${id}`, { nome, contato, endereco }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMensagem('✅ Cliente atualizado com sucesso!');
      setTimeout(() => navigate('/clientes'), 1500);
    } catch (err) {
      setMensagem('❌ Erro ao atualizar cliente');
      console.error(err);
    }
  };

  return (
    <div style={{ padding: '2rem', maxWidth: '500px', margin: 'auto' }}>
      <h2>Editar Cliente</h2>

      {carregando ? (
        <p>Carregando dados...</p>
      ) : (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Nome"
            value={nome}
            onChange={e => setNome(e.target.value)}
            required
          /><br /><br />
          <input
            type="text"
            placeholder="Contato"
            value={contato}
            onChange={e => setContato(e.target.value)}
            required
          /><br /><br />
          <input
            type="text"
            placeholder="Endereço"
            value={endereco}
            onChange={e => setEndereco(e.target.value)}
            required
          /><br /><br />
          <button type="submit">Salvar</button>
        </form>
      )}

      {!carregando && mensagem && (
        <p style={{ marginTop: '1rem', color: mensagem.includes('✅') ? 'green' : 'red' }}>
          {mensagem}
        </p>
      )}
    </div>
  );
}

export default EditarCliente;
