import React, { useEffect, useState } from 'react';
import api from '../services/api';
import { useNavigate } from 'react-router-dom';

// Função para remover acentos
const removerAcentos = (texto) => {
  return texto.normalize('NFD').replace(/[\u0300-\u036f]/g, '');
};

function Clientes() {
  const [clientes, setClientes] = useState([]);
  const [busca, setBusca] = useState('');
  const [mensagem, setMensagem] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const carregarClientes = async () => {
      const token = localStorage.getItem('token');
      try {
        const res = await api.get('/clientes', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setClientes(res.data);
      } catch (err) {
        console.error('Erro ao carregar clientes:', err);
        setMensagem('❌ Erro ao carregar clientes');
      }
    };

    carregarClientes();
  }, []);

  const excluirCliente = async (id) => {
    const confirmar = window.confirm('Tem certeza que deseja excluir este cliente?');
    if (!confirmar) return;

    const token = localStorage.getItem('token');
    try {
      await api.delete(`/clientes/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMensagem('✅ Cliente excluído com sucesso!');
      setClientes(clientes.filter(cliente => cliente.id !== id));
    } catch (err) {
      console.error('Erro ao excluir cliente:', err);
      setMensagem('❌ Este cliente possui vendas e não pode ser excluído');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Lista de Clientes</h2>

      <input
        type="text"
        placeholder="Buscar por nome..."
        value={busca}
        onChange={e => setBusca(e.target.value)}
        style={{ marginBottom: '1rem', padding: '0.5rem', width: '300px' }}
      />

      {mensagem && (
        <p style={{ color: mensagem.includes('✅') ? 'green' : 'red' }}>{mensagem}</p>
      )}

      <table border="1" cellPadding="10" style={{ width: '100%', marginTop: '1rem' }}>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Contato</th>
            <th>Endereço</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {clientes
            .filter(cliente =>
              removerAcentos(cliente.nome.toLowerCase()).includes(
                removerAcentos(busca.toLowerCase())
              )
            )
            .map(cliente => (
              <tr key={cliente.id}>
                <td>{cliente.nome}</td>
                <td>{cliente.contato}</td>
                <td>{cliente.endereco}</td>
                <td>
                  <button onClick={() => navigate(`/clientes/editar/${cliente.id}`)}>
                    Editar
                  </button>{' '}
                  <button
                    onClick={() => excluirCliente(cliente.id)}
                    style={{ marginLeft: '0.5rem', backgroundColor: 'red', color: 'white' }}
                  >
                    Excluir
                  </button>
                </td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
}

export default Clientes;
